import React, { Component } from 'react';
//import { Link } from 'react-router-dom';
import { Button, Row, Col, Card, Form, Modal } from 'reactstrap';
import axios from 'axios';
import UserList from './List-Users'
//import Select from 'react-select'

export default class ShowProjects extends Component {
  constructor(props){
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.handleChangeRole = this.handleChangeRole.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleShow = this.handleShow.bind(this);
    this.handleDelete = this.handleDelete.bind(this);

    this.state = {
      show: false,
      id: {},
      role: "",
      email: '',
      username: '',
      roles: [{ value: "Admin" , label: "Admin"},
              { value: "Devloper" , label: "Devloper"},
              { value: "Manager" , label: "Manager"}],
    } 
    this.usersStatic = []
  }

  componentDidMount() {
    axios.get('http://localhost:5000/api/users/'+this.props.match.params.id)
    .then(response => {
      this.setState({id: response.data._id})
      this.setState({username: response.data.username})
      this.setState({email: response.data.email})
      this.setState({role: response.data.role})
    })
    .catch((error) => {
      console.log(error);
    })
  }

  onChangeUsername(e){
    this.setState({username: e.target.value})
  }

  onChangeEmail(e){
    this.setState({email: e.target.value})
  }

  handleChangeRole(e){
    this.setState({role: e.value})
  }

  handleShow() {
    this.setState({show: !this.state.show})
  }

  //delete user, leave all tickets assigned to user however
  handleDelete() {
    axios.delete('http://localhost:5000/api/users/'+this.state.id)
    .then(res => {
      axios.post('http://localhost:5000/api/projects/removeoneuser/'+this.state.id)
    })
    .then(response => {
      window.location = '/users'
    })
  }

  //change user fields, update ticket assignedToUsername field
  handleSubmit(e) {
    e.preventDefault();
    const user = {
      id: this.state.id,
      username: this.state.username,     
      email: this.state.email,     
      role: this.state.role,        
    }

    axios.post('http://localhost:5000/api/users/update/'+this.props.match.params.id, user)
    .then(res => {
      axios.post('http://localhost:5000/api/tickets/updateassignment', user)
    })
    .then(res => {
      window.location = '/users'
    })
  }

  render() {
    return (
      <div>
      <Row>
        <Col sm={3}></Col>
        <Col>
          <Card style={{background: 'linear-gradient(to right bottom, #888888, #cccccc)', border: 0,padding: 15}}>
          <Form onSubmit={this.handleSubmit}>
            <h3 style={{color: 'white'}}>Edit User</h3>
            <div style={{color: 'white'}}><a href={"/tickets/details/"+this.props.match.params.id} style={{cursor: 'pointer'}}>Cancel</a> | <a href={"#"} onClick={this.handleShow}>Delete</a></div>
            <div style={{paddingTop:20, color: 'white'}}><h5>Username</h5></div>
            <div><Form.Control type="text" value={ this.state.username } onChange={this.onChangeUsername}/></div>
            <div style={{paddingTop:20, color: 'white'}}><h5>Email</h5></div>
            <div><Form.Control type="text" value={ this.state.email } onChange={this.onChangeEmail}/></div>
            <div style={{paddingTop:20, color: 'white'}}><h5>Role</h5></div> 
            <div><Select options={this.state.roles} value={{ value: this.state.role, label: this.state.role }} onChange={this.handleChangeRole}/></div>

            <div style={{paddingTop:20}}>
            <Button variant="primary" type="submit">
              Submit
            </Button>
            </div>

          </Form>
          </Card>
        </Col>
        <Col sm={3}></Col>
      </Row>

      <Modal show={this.state.show} onHide={this.handleShow}>
      <Modal.Body>Are you sure you want to delete this user?</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={this.handleShow}>
          Cancel
        </Button>
        <Button variant="danger" onClick={this.handleDelete}>
          Delete
        </Button>
      </Modal.Footer>
      </Modal>
      </div>
    )
  }
}
