import React, { Component } from 'react';
//import { Link } from 'react-router-dom';
import { Button,Container, Row, Col } from 'react-bootstrap';
//import axios from 'axios';
import ProjectList from './list-project.component'

export default class ShowProjects extends Component {

    constructor(props){
        super(props);

        this.state = {projects: [], grayRow: true};
    }

    componentDidMount() {}

    render() {
        return (
            <div style={{paddingLeft:0}}>
                    <Row>
                        <Col className="float-bottom" style={{paddingTop: 14}} ><h2>Projects</h2></Col>
                        <Col style={{paddingTop: 14}}>
                            <Button className="float-right"
                            onClick={() => {window.location.href = "/projects/create"}}
                            variant="primary"
                            size = "sm"
                            style={{margin: 0, marginBottom: 10, border: 0}} 
                            >
                            <h6 style={{paddingTop: 7, fontWeight: "bold", fontSize: 12}}>Create Project</h6>
                            </Button>
                        </Col>
                    </Row>
                <div style={{height: '85vh', overflowY: 'scroll'}}>
                <ProjectList />
                </div>
            </div>
        )
    }
}
