import React, { Component } from 'react';
import { Row, Col, } from 'react-bootstrap';
import ProjectCardlist from './Cardlist-Project';
import TicketCardlist from './Cardlist-Ticket';
import axios from 'axios';

export default class DetailsUser extends Component {
    constructor(props) {
        super(props);

        this.state = {
            lightgray: "#dddddd",
            midgray: "#bbbbbb",
            darkgray: "white",
            title: "",
            description: "",
            projects: [],
            tickets: [],
            user: {},
            userIds: [],
        } 
    }

    componentDidMount() {
        axios.get('http://localhost:5000/api/users/'+this.props.match.params.id)
        .then(response => {
          this.setState({user: response.data})
          var newIds = []
          response.map(user => {
            newIds.push(user._id)
          })
          this.setState({userIds: newIds})
        })
        .catch(function (error) {
            console.log(error);
            //window.location = '/users'
        })

        axios.get('http://localhost:5000/api/tickets/assignedto/'+this.props.match.params.id)
        .then(response => {
            console.log('response')
            console.log(response)
            if (response != null) {
                const newTickets = response.data.map(ticket => (
                    {description: ticket.description,
                    assignedToUsername: ticket.assignedToUsername,
                    project: ticket.project,
                    submiter: ticket.submiter,
                    status: ticket.status,
                    type: ticket.type,
                    id: ticket._id}))
                this.setState({tickets: newTickets})
            }
        })
    }

    render() {
      //var newdata = this.state.data;
      //var flip = false;
      let styles = {
        backgroundCard: {
            background: 'linear-gradient(to right bottom, #888888, #cccccc)', border: 0, height: "75vh",padding: 3, //overflowY: "scroll"
        }
      }
        return (
            <div>
            <h3> { this.state.user.username } </h3>
            <a href={"/projects/"} style={{cursor: 'pointer'}}>Back to List</a> | <a href={"/users/edit/"+this.props.match.params.id} style={{cursor: 'pointer'}}>Edit User</a>
              <Row style={{paddingTop:20,paddingBottom:5}}>
                <Col><h6> { this.state.user.email } </h6></Col>
              </Row>
              <Row style={{paddingBottom:5}}>
                <Col><h6> { this.state.user.role } </h6></Col>
              </Row>
              <Row >
                <Col sm={8}style={{backgroundColor: '', }}>
                  <ProjectCardlist id={this.props.match.params.id} height="72vh"/>
                </Col>
                <Col style={{backgroundColor: ''}}>
                  <TicketCardlist id={this.props.match.params.id} height="72vh"/>
                </Col>
              </Row>
            </div>
        );
    }
}
