import React, { Component } from 'react';
import { Container, Card } from 'reactstrap';
import axios from 'axios';
//import { app } from '../utils/axios-config';

//app.get("example");
//app.post("example");

export default class ProjectCardlist extends Component {
  constructor(props) {
    super(props);

    this.state = {
      height: this.props.height,
      projects: [],
    } 
  }

  componentDidMount() {
    if (this.props.id != null){

      //app.get
      axios.get('http://localhost:5000/api/projects/inproject/'+this.props.id)
      .then(response => {
        const newProjects = response.data.map(project => (
          {title: project.title,
          description: project.description,
          tickets: project.tickets.length,
          id: project._id,}))
        this.setState({projects: newProjects})
      })
      .catch(function (error) {
          console.log(error);
      })
    } else {
      axios.get('http://localhost:5000/api/projects')
      .then(response => {
        const newProjects = response.data.map(project => (
          {title: project.title,
          description: project.description,
          tickets: project.tickets.length,
          id: project._id,}))
        this.setState({projects: newProjects})
      })
      .catch(function (error) {
          console.log(error);
      })
    }
  }

  render() {
    //var newdata = this.state.data;
    //var flip = false;
    let styles = {
      backgroundCard: {
        background: 'linear-gradient(to right bottom, #888888, #cccccc)', border: 0, height: this.props.height,padding: 3, //overflowY: "scroll"
    }}
    return (
      <div>
        <Card style={styles.backgroundCard}>
        <h6 style={{paddingLeft: 17,paddingTop: 5}}>Projects</h6>
        <Card style={{backgroundColor: 'transparent' ,border: 0,height: this.props.height, overflowY: "scroll"}}>
        <Container style={{}}>
        {
          this.state.projects.map(project => {
            return(
            <Card onClick={() => {window.location.href = "/projects/details/"+project.id}} style={{backgroundColor: "white",border: 0,marginBottom:3,padding:5,color: 'black',cursor: 'pointer'}}>
              <h3>{project.title}</h3>
              <h6>{project.description}</h6>
              <div><div className='float-right'style={{fontSize:14}}>tickets: {project.tickets}</div>
              </div>
            </Card>) 
          })
        } 
        </Container>
        </Card>   
        </Card>   
      </div>
    );
  }
}
