import React, { Component } from 'react';
import { Button, Container, Row, Col, Card, Form } from 'react-bootstrap';
import Select from 'react-select'
import axios from 'axios';

export default class CreateUser extends Component {
    constructor(props) {
        super(props);

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.handleChangeRole = this.handleChangeRole.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            username: "",
            email: '',
            role: 'Devloper',
            roles: [{ value: "Admin" , label: "Admin"},
              { value: "Devloper" , label: "Devloper"},
              { value: "Manager" , label: "Manager"}],
        } 
    }

    onChangeUsername(e){
        this.setState({username: e.target.value})
    }

    onChangeEmail(e){
        this.setState({email: e.target.value})
    }

    handleChangeRole(e){
        this.setState({role: e.value})
    }

    onSubmit(e) {
        e.preventDefault();
        const user = {
            username: this.state.username,
            email: this.state.email,
            role: this.state.role,
        }

        axios.post("http://localhost:5000/api/users/add",user)
            .then(res => window.location = '/users');
    }

    render() {
        return (
            <div>
                <Row>
                <Col sm={3}></Col>
                <Col>
                <Card style={{        
                    background: 'linear-gradient(to right bottom,#888888, #cccccc)' , border: 0,padding: 15,
                }}>
                <h3>Create New User</h3>
                <div style={{color: 'white'}}><a href={"/users"} style={{cursor: 'pointer'}}>Cancel</a></div>

                <Form onSubmit={this.onSubmit}>
                    <label>Username: </label>
                    <Form.Control ref="text"
                        required
                        value={this.state.username}
                        onChange={this.onChangeUsername}
                        />
                    <label>Email: </label>
                    <Form.Control ref="text"
                        required
                        value={this.state.email}
                        onChange={this.onChangeEmail}
                        />
                    <label>Role: </label>
                    <div><Select options={this.state.roles} value={{ value: this.state.role, label: this.state.role }} onChange={this.handleChangeRole}/></div>
                    <div style={{paddingTop:20}}>
                    <Button variant="primary" type="submit">
                    Submit
                    </Button>
                    </div>
                </Form>
                </Card>  
                </Col>
                <Col sm={3}></Col>
                </Row>
            </div>
        )
    }
}
