import React, { Component } from 'react';
import { Button, Row, Col, Form, Modal, Card, } from 'react-bootstrap';
import axios from 'axios';

export default class EditProjectUsers extends Component {
  constructor(props) {
    super(props);

    this.handleSubmit = this.handleSubmit.bind(this)
    this.onChangeAddUsers = this.onChangeAddUsers.bind(this)
    this.onChangeRemoveUsers = this.onChangeRemoveUsers.bind(this)

    this.state = {
      lightgray: "#dddddd",
      midgray: "#bbbbbb",
      darkgray: "#555555",
      title: "",
      description: "",
      id: {},
      usersProject: [],
      usersToRemove: [],
      usersAll: [],
      usersToAdd: [],
    } 
  }

  //get project details
  componentDidMount() {
    axios.get('http://localhost:5000/api/projects/'+this.props.match.params.id)
    .then(response => {
      this.setState({title: response.data.title})
      this.setState({description: response.data.description})
      this.setState({id: response.data._id})
    })
    .catch(function (error) {
      console.log(error);
    })

    axios.get('http://localhost:5000/api/users/inproject/'+this.props.match.params.id)
    .then(response => {
      console.log('in project')
      console.log(response)

      var newUsers = []
      response.data.map(currentuser => {
        newUsers.push({ value: currentuser._id , label: currentuser.username})
      })
      this.setState({usersProject: newUsers})
    })


    axios.get('http://localhost:5000/api/users/notinproject/'+this.props.match.params.id)
      .then(response => {
        console.log('not in project')
        console.log(response)

        var newUsers = []
        response.data.map(currentuser => {
          newUsers.push({ value: currentuser._id , label: currentuser.username})
        })
        this.setState({usersAll: newUsers})
      })
    }

  //handle description text change
  onChangeRemoveUsers(e) {
    this.setState({usersToRemove: e})
  }

  //handle title text change
  onChangeAddUsers(e) {
    this.setState({usersToAdd: e})
  }

  //make project and send it to update project at id
  handleSubmit(e) {
    e.preventDefault()


    var newUsersToAdd = []
    var newUsersToRemove = []

    this.state.usersToAdd.map(currentuser => {
      newUsersToAdd.push(currentuser.value)
    })
    this.state.usersToRemove.map(currentuser => {
      newUsersToRemove.push(currentuser.value)
    })

    console.log('users to add')
    console.log(newUsersToAdd)
    console.log('users to remove')
    console.log(newUsersToRemove)

    const project = {
      id: this.state.id,
      usersToAdd: newUsersToAdd,
      usersToRemove: newUsersToRemove,
    }
    const projectAdd = {
      id: this.state.id,
      users: newUsersToAdd,
    }
    const projectRemove = {
      id: this.state.id,
      users: newUsersToRemove,
    }
    axios.post('http://localhost:5000/api/projects/addusers/'+this.props.match.params.id, project)
    .then(response => {
      axios.post('http://localhost:5000/api/projects/removeusers/'+this.props.match.params.id, project)
    })
    .then(response => {
      axios.post('http://localhost:5000/api/users/updateprojects/', projectAdd)
    })
    .then(response => {
      axios.post('http://localhost:5000/api/users/removeproject/', projectRemove)
    })
    .then(response => {
      window.location = '/projects/details/'+this.props.match.params.id
    })
  }

  render() {
    return (
      <div>
        <Row>
          <Col sm={3}></Col>
          <Col>
          <Card style={{        
            background: 'linear-gradient(to right bottom, #555555, #cccccc)' , border: 0,padding: 15,
          }}>
          <Form onSubmit={this.handleSubmit}>
          <h3 style={{color: 'white'}}>{this.state.title} Users</h3>
          <a href={"/projects/details/"+this.props.match.params.id} style={{cursor: 'pointer'}}>Cancel</a> 

            <div style={{paddingTop:20, color: 'white'}}><h5>Remove Users</h5></div>
            <div><Form.Select isMulti options={ this.state.usersProject } onChange={this.onChangeRemoveUsers}/></div>
            <div style={{paddingTop:20,color: 'white'}}><h5>Add Users</h5></div>
            <div><Form.Select isMulti options={ this.state.usersAll } onChange={this.onChangeAddUsers}/></div>

            <div style={{paddingTop:20}}>
            <Button variant="primary" type="submit">
              Submit
            </Button>
            </div>
            </Form>  
            </Card>      
          </Col>
          <Col sm={3}></Col>
        </Row>
      </div>
    );
  }
}
